async function removeElements(){
	let elements = root.children;
	let elementsToRemove = [];
	
	for(let i = 0; i < elements.length; i++){
		let element = elements[i];
		elementsToRemove.push(element);
	}

	for(let i = 0; i < elementsToRemove.length; i++){
		let element = elementsToRemove[i];
		element.remove();
	}

	await createTopBar();
};

window.onhashchange = async function(){
	hash = location.hash && location.hash.slice(1);

	await removeElements();

	if(loggedIn){
		switch (hash) {
			case `profile`:
				await loadProfile();
				break;
			case `directory`:
				await loadDirectory();
				break;
			case `dashboard`:
				await loadDashboard();
				break;
			case `posts`:
				await loadPosts();
				break;
			case `logout`:
				await logout();
				break;
			default:
				await loadDashboard();
				location.hash = `#dashboard`;
				break;
		}
	}else{
		switch (hash) {
			case `home`:
				await loadHome();
				break;
			case `register`:
				await loadRegister();
				break;
			case `login`:
				await loadLogin();
				break;
			case `directory`:
				await loadDirectory();
				break;
			default:
				await loadHome();
				location.hash = `#home`;
				break;
		}
	}
};

